import React from "react";
import {Link} from 'react-router-dom';

const Render = () => {

    const [renderMenu, setMenu] = React.useState(false)

    const NavSVG = () => {
        return (
            <svg 
            className="w-9 h-9" 
            fill="none" 
            stroke="currentColor" 
            viewBox="0 0 24 24" 
            xmlns="http://www.w3.org/2000/svg">
                <path 
                strokeLinecap="round" 
                strokeLinejoin="round" 
                strokeWidth="2" 
                d="M16 8v8m-4-5v5m-4-2v2m-2 4h12a2 2 0 002-2V6a2 2 0 00-2-2H6a2 2 0 00-2 2v12a2 2 0 002 2z">
                </path>
            </svg>
        );
    }

    const MenuSVG = () => {
        return (
            <svg 
            className="w-8 h-8 p-1 hover:bg-gray-200 rounded" 
            fill="none" 
            stroke="currentColor" 
            viewBox="0 0 24 24" 
            xmlns="http://www.w3.org/2000/svg">
                <path 
                strokeLinecap="round" 
                strokeLinejoin="round" 
                strokeWidth="3" 
                d="M4 6h16M4 12h16M4 18h7">
                </path>
            </svg>
        );
    }


    const Menu = () => {

        const CloseSVG = () => {
            return (
                <svg 
                className="w-8 h-8 p-1"
                fill="none" 
                stroke="currentColor" 
                viewBox="0 0 24 24" 
                xmlns="http://www.w3.org/2000/svg">
                    <path 
                    strokeLinecap="round" 
                    strokeLinejoin="round" 
                    strokeWidth="2" 
                    d="M6 18L18 6M6 6l12 12">
                    </path>
                </svg>
            );
        }

        return(
            <div 
            className='fixed bg-black inset-0 bg-opacity-30 overflow-hidden z-50'>
                <div 
                className='fixed flex flex-col bg-white w-2/3 inset-0 p-2'>
                    <div 
                    className='inline-flex items-center p-4 h-16 border-b border-gray-200 mb-8 -m-2'>
                        <div className='cursor-pointer hover:bg-red-200 hover:text-red-500 rounded p-1' onClick={() => setMenu(false)}><CloseSVG /></div>
                    </div>

                    <div 
                    className='flex flex-col space-y-3 text-lg font-semibold'>
                        <Link to='/' onClick={() => setMenu(false)} className='p-2 hover:bg-gray-200 rounded'>🏡 Home</Link>
                        <Link to='/forecast' onClick={() => setMenu(false)} className='p-2 hover:bg-gray-200 rounded'>📈 Forecasts</Link>
                        <Link to='/analysis' onClick={() => setMenu(false)} className='p-2 hover:bg-gray-200 rounded'>🔍 Analysis</Link>
                        <div className='p-2 text-gray-400 cursor-not-allowed'>🧑‍🚀 Members</div>
                        <div className='p-2 text-gray-400 cursor-not-allowed'>📚 FAQ</div>
                        <div className='p-2 text-gray-400 cursor-not-allowed'>🤓 About</div>
                    </div>
                </div>
            </div>
        );
    }

    const [user, setAuthUserExistence] = React.useState(false);

    React.useEffect(() => {
        const EstablishAuthAndUpdateState =
            auth.onAuthStateChanged(function(user) {
                if (user) {
                // User is signed in.
                setAuthUserExistence(true)
                } else {
                // No user is signed in.
                setAuthUserExistence(false)
                }
            })
        return () => EstablishAuthAndUpdateState();
    }, []);

    const Icon = () => {
        return (
            <Link
            to='/profile'
            className='relative self-end w-max bg-gray-100 p-4 rounded-full'>
                <p className='text-2xl absolute inset-0 inset-x-0.5'>🧐</p>
            </Link>
        );
    }

    return (
        <div 
        className='relative font-bold text-xl h-16 z-40'>
            <div 
            className='fixed inline-flex justify-start items-center space-x-3 border-b border-gray-200 bg-white w-full h-16'>
                { renderMenu ? <Menu /> : null }
                <div 
                className='px-3.5 cursor-pointer border-r' 
                onClick={() => setMenu(true)}>
                    <MenuSVG />
                </div>
                <Link 
                to='/' 
                className='appearance-none leading-none whitespace-pre text-sm inline-flex items-center hover:bg-gray-200 p-1 pr-2 rounded-lg'>
                    <NavSVG />
                    {data.nav.title}
                </Link>
                <div className='w-full flex flex-col justify-end px-3.5'>{ user ? <Icon /> : null }</div>
            </div>
        </div>
    )
}

export default Render
