import React from 'react'

const TrendingCoin = ({ coin }) => {
    return (
            <div className='bg-white rounded-lg flex flex-col border p-2'>
                <img className='w-32 my-4' src={coin.large} alt='logo'/>
                <div className='font-semibold text-lg'>{coin.name}</div>
            </div>
    )
}

const TrendingCoinList = ({ TrendingJSON }) => {
    var arrTrending = []
    TrendingJSON.forEach((coin) => {
        arrTrending.push(<TrendingCoin coin={coin} key={coin.id}/>)
    })
    return (
        <div>
            <div className='font-semibold text-2xl'>Trending Coins ðŸ”¥</div>
            <div className='font-medium text-lg'>A list of the most searched for crypto-coins in the past 24hrsðŸ“ˆðŸ‘€:</div>
            <div className='grid grid-flow-col grid-cols-3 grid-rows-3 gap-2'>
                {arrTrending}
            </div>
        </div>
    )
}

const Home = ({ TrendingJSON }) => {
    return (
        <div className='h-screen p-4'>
            <TrendingCoinList TrendingJSON={TrendingJSON}/>
        </div>
    )
}

export default Home
